window.eoxiaJS.noteDeFrais = {};

/**
 * Initialise l'objet note de frais ainsi que la méthode "init" obligatoire pour la bibliothèque EoxiaJS.
 *
 * @since 1.0.0.0
 * @version 1.0.0.0
 */

window.eoxiaJS.noteDeFrais.NDF = {};

/**
 * La méthode appelée automatiquement par la bibliothèque EoxiaJS.
 *
 * @return {void}
 *
 * @since 1.0.0.0
 * @version 1.0.0.0
 */

window.eoxiaJS.noteDeFrais.NDF.init = function() {
	jQuery( document ).on( 'click', '.validation_status.toggle .content .item', window.eoxiaJS.noteDeFrais.NDF.saveStatus );
	jQuery( document ).on( 'click', '.single-note .note .close', window.eoxiaJS.noteDeFrais.NDF.close );
};

window.eoxiaJS.noteDeFrais.NDF.refresh = function( triggeredElement, response ) {
	jQuery( '.single-note' ).html( response.data.view );
	if ( response.data.ndf ) {
		jQuery( '.note[data-id="' + response.data.ndf.id + '"] .ttc .value' ).text( response.data.ndf.tax_inclusive_amount );
		jQuery( '.note[data-id="' + response.data.ndf.id + '"] .tva .value' ).text( response.data.ndf.tax_amount );
		jQuery( '.note[data-id="' + response.data.ndf.id + '"] .update .value' ).text( response.data.ndf.date_modified.date_human_readable );
		jQuery( '.note[data-id="' + response.data.ndf.id + '"] .status .value' ).html( response.data.ndf.validation_status );
	}
};

window.eoxiaJS.noteDeFrais.NDF.open = function( triggeredElement, response ) {
	if ( response.data.main_view ) {
		jQuery( '.eox-note-frais' ).replaceWith( response.data.main_view );
	}
	jQuery( '.eox-note-frais' ).addClass( 'active-single' );
	window.eoxiaJS.noteDeFrais.NDF.refresh( triggeredElement, response );
};

window.eoxiaJS.noteDeFrais.NDF.close = function( event ) {
	jQuery( '.eox-note-frais' ).removeClass( 'active-single' );
};

window.eoxiaJS.noteDeFrais.NDF.saveStatus = function( event ) {
	if ( ( 'paye' === jQuery( this ).closest( 'li' ).data( 'type' ) ) && ! confirm( noteDeFrais.confirmMarkAsPayed ) ) {
		return false;
	}
	var toggle = jQuery( this ).closest( '.validation_status.toggle' );
	var type = jQuery( this ).closest( 'li' ).data( 'type' );
	var serialize = '';
	event.stopPropagation();
	toggle.find( '.label' ).html( jQuery( this ).text() );
	toggle.find( 'input[name="validation_status"]' ).val( jQuery( this ).text() );
	toggle.find( '.action .label' )[0].className = 'label pin-status ' + jQuery( this ).closest( 'li' ).data( 'type' );
	toggle.find( '.content' ).removeClass( 'active' );
	serialize = toggle.find( 'input' ).serialize();
	jQuery( '.single-note' ).find( '.date_modified_value' ).addClass( 'loading' );
	jQuery.post( ajaxurl, serialize, function( response ) {
		jQuery( '.note[data-id="' + response.data.ndf.id + '"] .status span.value' )[0].className = 'value pin-status ' + type;
		window.eoxiaJS.noteDeFrais.NDF.refresh( null, response );
	}, 'json' );
};

window.eoxiaJS.noteDeFrais.NDF.exportedNoteDeFraisSuccess = function( triggeredElement, response ) {
	window.eoxiaJS.global.downloadFile( response.data.link, response.data.filename );
};
window.eoxiaJS.noteDeFrais.NDF.archived = function( triggeredElement, response ) {
	jQuery( triggeredElement ).closest( '.note' ).fadeOut();
};

/**
 * Initialise l'objet "NDF" ainsi que la méthode "init" obligatoire pour la bibliothèque EoxiaJS.
 *
 * @since 1.0.0
 * @version 1.2.0
 */

window.eoxiaJS.noteDeFrais.NDFL = {};

/**
 * La méthode appelée automatiquement par la bibliothèque EoxiaJS.
 *
 * @return {void}
 *
 * @since 1.0.0
 * @version 1.2.0
 */
window.eoxiaJS.noteDeFrais.NDFL.init = function() {
	var currentFocusout = true;

	jQuery( document ).on( 'blur keyup paste keydown click', '.row.add li span[contenteditable]', window.eoxiaJS.noteDeFrais.NDFL.updateHiddenInput );

	jQuery( document ).on( 'click', '.row.add .action .ion-ios-plus', window.eoxiaJS.noteDeFrais.NDFL.saveNDF );
	jQuery( document ).on( 'keydown', '.row.add span[contenteditable]', function( event ) {
		if ( event.ctrlKey && 13 === event.keyCode ) {
			jQuery( this ).closest( '.row' ).find( '.action .ion-ios-plus' ).click();
		}
	} );
	jQuery( document ).on( 'keydown', '.row:not(.add) span[contenteditable]', function( event ) {
		if ( currentFocusout ) {
			jQuery( this ).focusout( function( event ) {
				jQuery( this ).each( window.eoxiaJS.noteDeFrais.NDFL.saveNDF );
				currentFocusout = true;
			} );
		}
		currentFocusout = false;
	} );
	jQuery( document ).on( 'keydown', '.libelle span', window.eoxiaJS.noteDeFrais.NDFL.focusSelect );
};

// Quand on change de date dans le calendrier.
window.eoxiaJS.noteDeFrais.NDFL.changeDate = function( element ) {
	// On affiche date sélectionnée dans le "champs" contenteditable.
	element.closest( '.group-date' ).find( 'span[contenteditable="true"]' ).text( window.eoxiaJS.date.convertMySQLDate( element.val(), false ) );

	// Si on est dans le cadre d'une édition de ligne alors on lance l'enregistrement
	if ( ! element.closest( 'ul.row' ).hasClass( 'add' ) ) {
		element.each( window.eoxiaJS.noteDeFrais.NDFL.saveNDF );
	}
};

window.eoxiaJS.noteDeFrais.NDFL.updateHiddenInput = function( event ) {
	if ( 0 >= jQuery( this ).text().length ) {
		jQuery( this ).closest( 'li' ).find( '.ndfl-placeholder' ).addClass( 'hidden' );
	}
};

window.eoxiaJS.noteDeFrais.NDFL.beforeDisplayModeChange = function( element ) {
	var displayTypeToActivate = jQuery( element ).attr( 'data-display-mode' );
	var mainContainer = jQuery( element ).closest( 'div.container' ).find( '.flex-table' );

	if ( ! mainContainer.hasClass( displayTypeToActivate ) ) {
		return true;
	} else {
		return false;
	}
};

window.eoxiaJS.noteDeFrais.NDFL.confirmDeletion = function( element ) {
	return confirm( jQuery( element ).data().confirmText );
};

window.eoxiaJS.noteDeFrais.NDFL.saveNDF = function( event ) {
	var name, extra, value = '';
	var serialize = jQuery( '.single-note .note' ).not( '.row' ).children( 'input' ).serialize();
	if ( 0 !== serialize.length ) {
		serialize += '&';
	}
	jQuery( this ).closest( '.row' ).find( 'input' ).each( function() {
		if ( 0 !== serialize.length ) {
			serialize += '&';
		}
		name = jQuery( this ).serialize().substr( 0, jQuery( this ).serialize().indexOf( '=' ) );
		extra = name.substr( ( ( -1 === name.indexOf( '%5B' ) ) ? name.length : name.indexOf( '%5B' ) ) );
		name = name.substr( 0, ( ( -1 === name.indexOf( '%5B' ) ) ? name.length : name.indexOf( '%5B' ) ) );
		value = jQuery( this ).serialize().substr( jQuery( this ).serialize().indexOf( '=' ) );
		serialize += 'row[' + jQuery( this ).closest( '.row' ).data( 'i' ) + '][' + name + ']' + extra + value;
	} );
	jQuery( this ).closest( '.row' ).find( 'span[contenteditable]' ).each( function( index ) {
		if ( undefined !== jQuery( this ).data( 'name' ) ) {
			if ( 0 !== serialize.length ) {
				serialize += '&';
			}
			serialize += jQuery( this ).data( 'name' ) + '=' + jQuery( this ).text();
		}
	} );
	jQuery( this ).closest( '.row' ).addClass( 'loading' );
	jQuery( '.single-note' ).find( '.date_modified_value' ).addClass( 'loading' );
	jQuery.post( ajaxurl, serialize, function( response ) {
		window.eoxiaJS.noteDeFrais.NDF.refresh( null, response );
	}, 'json' );
};

window.eoxiaJS.noteDeFrais.NDFL.focusSelect = function( event ) {
	var code = ( event.keyCode ? event.keyCode : event.which );
	if ( 9 == code ) {
		event.preventDefault();
		jQuery( this ).blur();
		jQuery( this ).closest( '.row' ).find( '.toggle .content' ).addClass( 'active' );
	}
};

/**
 * Initialise l'objet "typeNote" ainsi que la méthode "init" obligatoire pour la bibliothèque EoxiaJS.
 *
 * @since 1.2.0
 * @version 1.2.0
 */

window.eoxiaJS.noteDeFrais.typeNote = {};

/**
 * La méthode appelée automatiquement par la bibliothèque EoxiaJS.
 * *
 * @since 1.2.0
 * @version 1.2.0
 *
 * @return {void}
 */
window.eoxiaJS.noteDeFrais.typeNote.init = function() {
	window.eoxiaJS.noteDeFrais.typeNote.event();
};

/**
 * Gestion des évènements
 *
 * @since 1.2.0
 * @version 1.2.0
 *
 * @return {void}
 */
window.eoxiaJS.noteDeFrais.typeNote.event = function() {
	jQuery( document ).on( 'click', '.content .toggle .content .item', window.eoxiaJS.noteDeFrais.typeNote.select );
};

/**
 * Change le 'lable' du toggle.
 *
 * @since 1.2.0
 * @version 1.2.0
 *
 * @param  {ClickEvent} event L'état lors du clic.
 * @return {void}
 *
 */
window.eoxiaJS.noteDeFrais.typeNote.select = function( event ) {
	var row = jQuery( this ).closest( '.row' );
	event.stopPropagation();
	row.find( '.toggle .label' ).text( jQuery( this ).text() );
	row.find( '.toggle input' ).val( jQuery( this ).data( 'id' ) );
	row.find( '.toggle .content' ).removeClass( 'active' );
	if ( ! row.hasClass( 'add' ) ) {
		jQuery( this ).each( window.eoxiaJS.noteDeFrais.NDFL.saveNDF );
	}

	window.eoxiaJS.noteDeFrais.typeNote.setStateField( row, 'km', false );
	window.eoxiaJS.noteDeFrais.typeNote.setStateField( row, 'ttc', true );
	window.eoxiaJS.noteDeFrais.typeNote.setStateField( row, 'tva', true );

	if ( jQuery( this ).data( 'special-treatment' ) ) {
		window.eoxiaJS.noteDeFrais.typeNote[ jQuery( this ).data( 'special-treatment' ) ]( row );
	}
};

window.eoxiaJS.noteDeFrais.typeNote.km_calculation = function( row ) {
	window.eoxiaJS.noteDeFrais.typeNote.setStateField( row, 'km', true );
	window.eoxiaJS.noteDeFrais.typeNote.setStateField( row, 'ttc', false );
	window.eoxiaJS.noteDeFrais.typeNote.setStateField( row, 'tva', false );
};

/**
 * Rend un 'contenteditable' editable ou pas.
 *
 * @since 1.2.0
 * @version 1.2.0
 *
 * @param  {HTMLUListElement} element L'élément contenant les champs 'contenteditable'.
 * @param  {string} nameField         Le champ contenteditable en question.
 * @param  {boolean} enabled          Rendre enabled ou pas.
 * @return {void}
 */
window.eoxiaJS.noteDeFrais.typeNote.setStateField = function( element, nameField, enabled ) {
	element.find( '.' + nameField + ' span[contenteditable]' ).attr( 'contenteditable', enabled );

	if ( enabled ) {
		element.find( '.' + nameField ).removeClass( 'disabled' );
	} else {
		element.find( '.' + nameField ).addClass( 'disabled' );
	}
};

/**
 * Initialise l'objet "upload" ainsi que la méthode "init" obligatoire pour la bibliothèque EoxiaJS.
 *
 * @since 0.1.0-alpha
 * @version 1.2.0
 * @copyright 2017
 * @author Jimmy Latour <jimmy@eoxia.com>
 */

window.eoxiaJS.upload = {};

/**
 * Keep the button in memory.
 *
 * @type {Object}
 */
window.eoxiaJS.upload.currentButton;

/**
 * Keep the media frame in memory.
 * @type {Object}
 */
window.eoxiaJS.upload.mediaFrame;

/**
 * Keep the selected media in memory.
 * @type {Object}
 */
window.eoxiaJS.upload.selectedInfos = {
	JSON: undefined,
	fileID: undefined
};

/**
 * Init func.
 *
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.1.0-alpha
 */
window.eoxiaJS.upload.init = function() {
	window.eoxiaJS.upload.event();
};

/**
 * Event func.
 *
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 1.2.0
 */
window.eoxiaJS.upload.event = function() {
	jQuery( document ).on( 'click', '.media:not(.loading), .wpeo-upload-list a.upload:not(.loading)', window.eoxiaJS.upload.openPopup );
	jQuery( document ).on( 'click', '.media-toolbar, .media-modal-close, .media-button-insert', function( event ) { event.stopPropagation(); } );
};

/**
 * Open the media frame from WordPress or the custom gallery.
 *
 * @param  {MouseEvent} event  The mouse state.
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.2.0-alpha
 */
window.eoxiaJS.upload.openPopup = function( event ) {
	window.eoxiaJS.upload.currentButton = jQuery( this );
	event.preventDefault();

	if ( jQuery( this ).hasClass( 'no-file' ) || jQuery( this ).is( "a" ) ) {
		window.eoxiaJS.upload.openMediaFrame();
	} else {
		window.eoxiaJS.gallery.open();
	}
};

/**
 * Open the media frame from WordPress.
 *
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.2.0-alpha
 */
window.eoxiaJS.upload.openMediaFrame = function() {
	window.eoxiaJS.upload.mediaFrame = new window.wp.media.view.MediaFrame.Post({
		'library':{
			'type': window.eoxiaJS.upload.currentButton.data( 'mime-type' )
		}
	}).open();
	window.eoxiaJS.upload.mediaFrame.on( 'insert', function() { window.eoxiaJS.upload.selectedFile(); } );
};

/**
 * Get the media selected and call associateFile.
 *
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.2.0-alpha
 */
window.eoxiaJS.upload.selectedFile = function() {
	window.eoxiaJS.upload.mediaFrame.state().get( 'selection' ).map( function( attachment ) {
		window.eoxiaJS.upload.selectedInfos.JSON = attachment.toJSON();
		window.eoxiaJS.upload.selectedInfos.id = attachment.id;
	} );
	window.eoxiaJS.upload.associateFile();
};

/**
 * Make request for associate file
 *
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.2.0-alpha
 */
window.eoxiaJS.upload.associateFile = function() {
	var data = {
		action: 'eo_upload_associate_file',
		file_id: window.eoxiaJS.upload.selectedInfos.id
	};
	var key = '';
	window.eoxiaJS.upload.currentButton.get_data( function( attrData ) {
		for ( key in attrData ) {
			data[key] = attrData[key];
		}
	} );
	window.eoxiaJS.upload.currentButton.addClass( 'loading' );
	jQuery.post( window.ajaxurl, data, function( response ) {
		window.eoxiaJS.upload.refreshButton( response.data );

		if ( 'gallery' === response.data.display_type ) {
			window.eoxiaJS.gallery.open( false );
		}
	} );
};

/**
 * Update the view of the button
 *
 * @param  {Object} data Data of button.
 * @return {void}
 *
 * @since 0.1.0-alpha
 * @version 1.2.0
 */
window.eoxiaJS.upload.refreshButton = function( data ) {
	if( window.eoxiaJS.upload.currentButton.is( 'a' ) ) {
		window.eoxiaJS.upload.currentButton.removeClass( 'loading' );
		if ( ! data.id ) {
			window.eoxiaJS.upload.currentButton.closest( 'div' ).find( 'ul' ).append( data.view );
		}
	} else {
		if ( data.view ) {
			if ( window.eoxiaJS.upload.currentButton.data( 'custom-class' ) ) {
				jQuery( 'span.media[data-id="' + window.eoxiaJS.upload.currentButton.data( 'id' ) + '"].' + window.eoxiaJS.upload.currentButton.data( 'custom-class' ) ).replaceWith( data.view );
			} else {
				jQuery( 'span.media[data-id="' + window.eoxiaJS.upload.currentButton.data( 'id' ) + '"]' ).replaceWith( data.view );
			}
		} else {
			window.eoxiaJS.upload.currentButton.find( 'img' ).replaceWith( data.media );
			window.eoxiaJS.upload.currentButton.find( 'i' ).hide();
			window.eoxiaJS.upload.currentButton.find( 'input[type="hidden"]' ).val( window.eoxiaJS.upload.selectedInfos.JSON.id );
		}
	}
};

window.eoxiaJS.gallery = {};

/**
 * Init func.
 *
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.1.0-alpha
 */
window.eoxiaJS.gallery.init = function() {
	window.eoxiaJS.gallery.event();
};

/**
 * Event func.
 *
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.1.0-alpha
 */
window.eoxiaJS.gallery.event = function() {
	jQuery( document ).on( 'keyup', window.eoxiaJS.gallery.keyup );
	jQuery( document ).on( 'click', '.eo-gallery', function( event ) { event.preventDefault(); return false; } );
	jQuery( document ).on( 'click', '.eo-gallery .navigation .prev', window.eoxiaJS.gallery.prevPicture );
	jQuery( document ).on( 'click', '.eo-gallery .navigation .next', window.eoxiaJS.gallery.nextPicture );
	jQuery( document ).on( 'click', '.eo-gallery .close', window.eoxiaJS.gallery.close );
};

/**
 * Make request for open gallery
 *
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.2.0-alpha
 */
window.eoxiaJS.gallery.open = function( append = true ) {
	var data = {
		action: 'eo_upload_load_gallery'
	};
	var key = '';
	window.eoxiaJS.upload.currentButton.get_data( function( attrData ) {
		for ( key in attrData ) {
			data[key] = attrData[key];
		}
	} );
	window.eoxiaJS.upload.currentButton.addClass( 'loading' );

	if ( append ) {
		jQuery( '.eo-gallery' ).remove();
	}

	jQuery.post( ajaxurl, data, function( response ) {
		if ( append ) {
			jQuery( '#wpwrap' ).append( response.data.view );
		} else {
			jQuery( '.eo-gallery' ).replaceWith( response.data.view );
		}
		window.eoxiaJS.upload.currentButton.removeClass( 'loading' );
	});
};

/**
 * Next and Previous picture in gallery
 *
 * @param  {KeyEvent} event Keyboard state.
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.1.0-alpha
 */
window.eoxiaJS.gallery.keyup = function( event ) {
	if ( 37 === event.keyCode ) {
		window.eoxiaJS.gallery.prevPicture();
	} else if ( 39 === event.keyCode ) {
		window.eoxiaJS.gallery.nextPicture();
	} else if ( 27 === event.keyCode ) {
		jQuery( '.eo-gallery .close' ).click();
	}
};

/**
 * Prev picture func.
 *
 * @param  {KeyEvent} event Keyboard state.
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.1.0-alpha
 */
window.eoxiaJS.gallery.prevPicture = function( event ) {
	if ( jQuery( '.eo-gallery .image-list li.current' ).prev().length <= 0 ) {
		jQuery( '.eo-gallery .image-list li.current' ).toggleClass( 'current hidden' );
		jQuery( '.eo-gallery .image-list li:last' ).toggleClass( 'hidden current' );
	}	else {
		jQuery( '.eo-gallery .image-list li.current' ).toggleClass( 'current hidden' ).prev().toggleClass( 'hidden current' );
	}

	jQuery( '.eo-gallery .edit-thumbnail-id' ).attr( 'data-file-id', jQuery( '.eo-gallery .current' ).attr( 'data-id' ) );
};

/**
 * Next picture func.
 *
 * @param  {KeyEvent} event Keyboard state.
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.1.0-alpha
 */
window.eoxiaJS.gallery.nextPicture = function( event ) {
	if ( jQuery( '.eo-gallery .image-list li.current' ).next().length <= 0 ) {
		jQuery( '.eo-gallery .image-list li.current' ).toggleClass( 'current hidden' );
		jQuery( '.eo-gallery .image-list li:first' ).toggleClass( 'hidden current' );
	} else {
		jQuery( '.eo-gallery .image-list li.current' ).toggleClass( 'current hidden' ).next().toggleClass( 'hidden current' );
	}

	jQuery( '.eo-gallery .edit-thumbnail-id' ).attr( 'data-file-id', jQuery( '.eo-gallery .current' ).attr( 'data-id' ) );
};

/**
 * Close the gallery
 *
 * @param  {KeyEvent} event Keyboard state
 * @return void
 *
 * @since 0.1.0-alpha
 * @version 0.1.0-alpha
 */
window.eoxiaJS.gallery.close = function( event ) {
	jQuery( '.eo-gallery' ).remove();
};

/**
 * Le callback en cas de réussite à la requête Ajax "dissociate_file".
 * Remplaces les boutons pour ouvrir la popup "galerie"
 *
 * @param  {HTMLDivElement} triggeredElement  L'élement HTML déclenchant la requête Ajax.
 * @param  {Object}         response          Les données renvoyées par la requête Ajax.
 * @return {void}
 *
 * @since 0.1.0-alpha
 * @version 0.2.0-alpha
 */
window.eoxiaJS.gallery.dissociatedFileSuccess = function( element, response ) {
	if ( response.data.close_popup ) {
		jQuery( '.eo-gallery' ).remove();
	}

	jQuery( '.eo-gallery .image-list .current' ).remove();
	jQuery( '.eo-gallery .prev' ).click();
	window.eoxiaJS.upload.refreshButton( response.data );
};

/**
 * Le callback en cas de réussite à la requête Ajax "eo_set_thumbnail".
 * Remplaces les boutons pour ouvrir la popup "galerie"
 *
 * @param  {HTMLDivElement} triggeredElement  L'élement HTML déclenchant la requête Ajax.
 * @param  {Object}         response          Les données renvoyées par la requête Ajax.
 * @return {void}
 *
 * @since 0.1.0-alpha
 * @version 0.1.0-alpha
 */
window.eoxiaJS.gallery.successfulSetThumbnail = function( element, response ) {
	window.eoxiaJS.upload.refreshButton( response.data );
};
